    public static int[] removeDuplicates(int[] arr) {
    
        int end = arr.length;
    
        for (int i = 0; i &lt; end; i++) {
            for (int j = i + 1; j &lt; end; j++) {
                if (arr[i] == arr[j]) {                  
                    int shiftLeft = j;
                    for (int k = j+1; k &lt; end; k++, shiftLeft++) {
                        arr[shiftLeft] = arr[k];
                    }
                    end--;
                    j--;
                }
            }
        }
    
        int[] whitelist = new int[end];
        for(int i = 0; i &lt; end; i++){
            whitelist[i] = arr[i];
        }
        return whitelist;
    }

