            &lt;property name=&quot;paramName&quot; value=&quot;lang&quot;/&gt;
        &lt;/bean&gt;
    &lt;/mvc:interceptors&gt;

    &lt;!--&lt;bean class=&quot;nz.net.ultraq.thymeleaf.LayoutDialect&quot;&gt;--&gt;
    &lt;!--&lt;constructor-arg ref=&quot;groupingStrategy&quot;/&gt;--&gt;
    &lt;!--&lt;/bean&gt;--&gt;
    &lt;!--************************************************************************
        PROPERTY PLACEHOLDER CONFIGURATIONS
