        &lt;property name=&quot;password&quot; value=&quot;${mysql.connection.password}&quot;/&gt;
        &lt;!--C3P0 properties--&gt;
        &lt;property name=&quot;acquireIncrement&quot; value=&quot;${c3p0.acquire.increment}&quot;/&gt;
        &lt;property name=&quot;minPoolSize&quot; value=&quot;${c3p0.min.pool.size}&quot;/&gt;
