	public static List&lt;String&gt; splitString(String str) {
		if (str == null) {
			return null;
		}
		Pattern wordPattern = Pattern.compile(WORD_PATTERN);
		Matcher wordMatcher = wordPattern.matcher(str);
		
		List&lt;String&gt; splitString = new ArrayList&lt;&gt;();
		
		int endOfLastWord = 0;
		
		while(wordMatcher.find())
